import{_ as t,c as e,o as p,a as i}from"./app.218f227b.js";const g=JSON.parse('{"title":"\u8BC1\u4E66\u4E3A\u4EC0\u4E48\u53EF\u4EE5\u9632\u5192\u5145\uFF1F","description":"","frontmatter":{"title":"\u8BC1\u4E66\u4E3A\u4EC0\u4E48\u53EF\u4EE5\u9632\u5192\u5145\uFF1F","date":"2018-09-26T13:00:28.000Z","sidebar":"auto","tags":["https"],"categories":["\u7F51\u7EDC\u534F\u8BAE"],"author":"\u5728\u4E0B\u6668\u6EAA"},"headers":[{"level":2,"title":"\u7B2C\u4E00\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u6570\u636E\uFF09","slug":"\u7B2C\u4E00\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u6570\u636E\uFF09","link":"#\u7B2C\u4E00\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u6570\u636E\uFF09","children":[]},{"level":2,"title":"\u7B2C\u4E8C\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u5BC6\u94A5\uFF09","slug":"\u7B2C\u4E8C\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u5BC6\u94A5\uFF09","link":"#\u7B2C\u4E8C\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u5BC6\u94A5\uFF09","children":[]},{"level":2,"title":"\u7B2C\u4E09\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u516C\u94A5\uFF09\u{1F31F}","slug":"\u7B2C\u4E09\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u516C\u94A5\uFF09\u{1F31F}","link":"#\u7B2C\u4E09\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u516C\u94A5\uFF09\u{1F31F}","children":[]},{"level":2,"title":"\u7B2C\u56DB\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u8BC1\u4E66\uFF09\u{1F31F}","slug":"\u7B2C\u56DB\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u8BC1\u4E66\uFF09\u{1F31F}","link":"#\u7B2C\u56DB\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u8BC1\u4E66\uFF09\u{1F31F}","children":[]}],"relativePath":"network-protocol/16.https\u8BC1\u4E66.md","lastUpdated":1661883935000}'),l={name:"network-protocol/16.https\u8BC1\u4E66.md"},a=i('<p>\u57FA\u4E8E<strong>\u4E3A\u4EC0\u4E48 CA \u7684\u8BC1\u4E66\u53EF\u4EE5\u4F7F\u5F97\u516C\u94A5\u6B63\u786E\u4F20\u8F93\u7ED9\u5BA2\u6237\u7AEF</strong>\uFF0C\u4E0D\u662F\u5F88\u597D\u7406\u89E3\uFF0C\u8FD9\u91CC\u518D\u89E3\u6790\u4E00\u4E0B\u3002\u8F6C\u81EA <a href="https://zhuanlan.zhihu.com/p/25976060" target="_blank" rel="noreferrer">\u770B\u5B8C\u8FD8\u4E0D\u61C2 HTTPS \u6211\u76F4\u64AD\u5403\u7FD4</a></p><p>https \u901A\u4FE1\u5B8C\u6574\u6D41\u7A0B\u56FE\uFF1A</p><p><img src="https://alvin-cdn.oss-cn-shenzhen.aliyuncs.com/images/https-certificate1.png" alt=""></p><h2 id="\u7B2C\u4E00\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u6570\u636E\uFF09" tabindex="-1">\u7B2C\u4E00\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u6570\u636E\uFF09 <a class="header-anchor" href="#\u7B2C\u4E00\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u6570\u636E\uFF09" aria-hidden="true">#</a></h2><p>\u5047\u5982\u6211\u4EEC\u8981\u5B9E\u73B0\u4E00\u4E2A\u529F\u80FD\uFF1A\u4E00\u4E2A\u7528\u6237 A \u7ED9\u4E00\u4E2A\u7528\u6237 B \u53D1\u6D88\u606F\uFF0C\u4F46\u662F\u8981\u4FDD\u8BC1\u8FD9\u4E2A\u6D88\u606F\u7684\u5185\u5BB9\u53EA\u80FD\u88AB A \u548C B \u77E5\u9053\uFF0C\u5176\u4ED6\u7684\u65E0\u8BBA\u662F\u58A8\u6E0A\u4E0A\u795E\u8FD8\u662F\u592A\u4E0A\u8001\u541B\u90FD\u6CA1\u529E\u6CD5\u7834\u89E3\u6216\u8005\u7BE1\u6539\u6D88\u606F\u7684\u5185\u5BB9\u3002</p><p>\u7531\u4E8E\u6D88\u606F\u4E0D\u60F3\u88AB\u5176\u4ED6\u4EBA\u770B\u5230\uFF0C\u6240\u4EE5\u6211\u4EEC\u81EA\u7136\u800C\u7136\u5C31\u4F1A\u60F3\u5230\u4E3A\u6D88\u606F\u52A0\u5BC6\uFF0C\u5E76\u4E14\u53EA\u6709 A \u548C B \u624D\u6709\u89E3\u5BC6\u7684\u5BC6\u94A5\u3002\u8FD9\u91CC\u9700\u8981\u8003\u8651\u51E0\u70B9:</p><ol><li>\u4F7F\u7528\u4EC0\u4E48\u52A0\u5BC6\u65B9\u5F0F\uFF1F</li><li>\u5BC6\u94A5\u600E\u4E48\u544A\u77E5\u5BF9\u65B9\uFF1F</li></ol><p>\u5BF9\u4E8E\u7B2C\u4E00\u4E2A\u95EE\u9898\uFF0C\u52A0\u5BC6\u7B97\u6CD5\u5206\u4E3A\u4E24\u7C7B\uFF1A\u5BF9\u79F0\u52A0\u5BC6\u548C\u975E\u5BF9\u79F0\u52A0\u5BC6\uFF0C\u8FD9\u91CC\u6211\u4EEC\u9009\u62E9\u5BF9\u79F0\u673A\u5BC6\uFF0C\u539F\u56E0\u6709\u5982\u4E0B\u51E0\u4E2A\uFF1A</p><ol><li>\u5BF9\u79F0\u52A0\u5BC6\u901F\u5EA6\u5FEB\uFF0C\u52A0\u5BC6\u65F6 CPU \u8D44\u6E90\u6D88\u8017\u5C11\uFF1B</li><li>\u975E\u5BF9\u79F0\u52A0\u5BC6\u5BF9\u5F85\u52A0\u5BC6\u7684\u6570\u636E\u7684\u957F\u5EA6\u6709\u6BD4\u8F83\u4E25\u683C\u7684\u8981\u6C42\uFF0C\u4E0D\u80FD\u592A\u957F\uFF0C\u4F46\u662F\u5B9E\u9645\u4E2D\u6D88\u606F\u53EF\u80FD\u4F1A\u5F88\u957F\uFF0C\u56E0\u6B64\u975E\u5BF9\u79F0\u52A0\u5BC6\u5C31\u6EE1\u8DB3\u4E0D\u4E86\uFF1B</li></ol><p>\u5BF9\u4E8E\u7B2C\u4E8C\u4E2A\u95EE\u9898\uFF0C\u8FD9\u662F\u5BFC\u81F4\u6574\u4E2A https \u901A\u4FE1\u8FC7\u7A0B\u5F88\u590D\u6742\u7684\u6839\u672C\u539F\u56E0\u3002 \u5982\u679C A \u6216 B \u76F4\u63A5\u628A\u4ED6\u4EEC\u4E4B\u95F4\u7528\u4E8E\u89E3\u5BC6\u7684\u5BC6\u94A5\u901A\u8FC7\u4E92\u8054\u7F51\u4F20\u8F93\u7ED9\u5BF9\u65B9\uFF0C\u90A3\u4E00\u65E6\u5BC6\u94A5\u88AB\u7B2C\u4E09\u8005\u52AB\u6301\uFF0C\u7B2C\u4E09\u8005\u5C31\u80FD\u6B63\u786E\u89E3\u5BC6 A,B \u4E4B\u95F4\u7684\u901A\u4FE1\u6570\u636E\u3002</p><h2 id="\u7B2C\u4E8C\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u5BC6\u94A5\uFF09" tabindex="-1">\u7B2C\u4E8C\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u5BC6\u94A5\uFF09 <a class="header-anchor" href="#\u7B2C\u4E8C\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u5BC6\u94A5\uFF09" aria-hidden="true">#</a></h2><p>\u901A\u8FC7\u7B2C\u4E00\u5C42\u7684\u63CF\u8FF0\uFF0C\u7B2C\u4E8C\u5C42\u9700\u8981\u89E3\u51B3\u7684\u95EE\u9898\u662F\uFF1A<strong>\u5B89\u5168\u5730\u4F20\u8F93 A,B \u4E4B\u95F4\u7528\u4E8E\u89E3\u5BC6\u6570\u636E\u7684\u5BC6\u94A5</strong>\u3002</p><p>\u56E0\u4E3A\u5982\u679C\u4F20\u8F93\u8FC7\u7A0B\u4E2D\u8FD9\u628A\u5BC6\u94A5\u88AB\u7B2C\u4E09\u8005\u62FF\u5230\u4E86\uFF0C\u5C31\u80FD\u89E3\u5BC6\u4F20\u901A\u4FE1\u6570\u636E\uFF0C\u6240\u4EE5\uFF0C\u8FD9\u628A\u5BC6\u94A5\u5FC5\u987B\u5F97\u52A0\u5BC6\uFF0C\u5C31\u7B97\u7B2C\u4E09\u8005\u52AB\u6301\u5230\u8FD9\u628A\u52A0\u5BC6\u8FC7\u7684\u5BC6\u94A5\uFF0C\u4ED6\u4E5F\u4E0D\u80FD\u89E3\u5BC6\uFF0C\u5F97\u5230\u771F\u6B63\u7684\u5BC6\u94A5\u3002</p><p>\u90A3\u5C31\u662F\u7528\u975E\u5BF9\u79F0\u52A0\u5BC6\u54AF\uFF0C\u90A3\u5982\u4F55\u5E94\u7528\u975E\u5BF9\u79F0\u52A0\u5BC6\u6765\u52A0\u5BC6\u90A3\u628A\u5BC6\u94A5\u5462\uFF1F</p><p>\u8003\u8651\u5982\u4E0B\u65B9\u5F0F\uFF1A</p><p><img src="https://alvin-cdn.oss-cn-shenzhen.aliyuncs.com/images/https-certificate2.png" alt=""></p><p>\u5206\u6790\u4E00\u4E0B\u4E0A\u9762\u6B65\u9AA4\u7684\u53EF\u884C\u6027\uFF1A</p><ul><li>\u4E0A\u8FF0\u6B65\u9AA4\u4E2D\u6700\u7EC8\u7528\u4E8E\u52A0\u5BC6\u6570\u636E\u7684\u5BC6\u94A5\u662F\u5BA2\u6237\u7AEF\u751F\u6210\u5E76\u4E14\u7528\u516C\u94A5\u52A0\u5BC6\u4E4B\u540E\u4F20\u7ED9\u670D\u52A1\u5668\u7684\uFF0C\u56E0\u4E3A\u79C1\u94A5\u53EA\u6709\u670D\u52A1\u5668\u624D\u6709\uFF0C\u6240\u4EE5\u4E5F\u5C31\u53EA\u6709\u670D\u52A1\u5668\u624D\u80FD\u89E3\u5F00\u5BA2\u6237\u7AEF\u4E0A\u62A5\u7684\u5BC6\u94A5\uFF1B</li><li>\u8981\u4FDD\u8BC1\u4F20\u8F93\u7684\u5BC6\u94A5\u53EA\u80FD\u88AB\u670D\u52A1\u5668\u89E3\u5BC6\uFF0C\u5C31\u5F97\u4FDD\u8BC1\u7528\u4E8E\u52A0\u5BC6\u5BC6\u94A5\u7684\u516C\u94A5\u4E00\u5B9A\u662F\u670D\u52A1\u5668\u4E0B\u53D1\u7684\uFF0C\u7EDD\u5BF9\u4E0D\u53EF\u80FD\u88AB\u7B2C\u4E09\u65B9\u7BE1\u6539\u8FC7\uFF1B</li></ul><p>\u56E0\u4E3A\u8FD8\u53EF\u80FD\u5B58\u5728\u4E00\u79CD&quot;\u4E2D\u95F4\u4EBA\u653B\u51FB&quot;\u7684\u60C5\u51B5\uFF0C\u5982\u4E0B\u56FE\uFF1A</p><p><img src="https://alvin-cdn.oss-cn-shenzhen.aliyuncs.com/images/https-certificate3.png" alt=""></p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\u4E4B\u95F4\u901A\u4FE1\u7684\u6570\u636E\u5C31\u5B8C\u5168\u88AB\u574F\u4EBA\u7834\u89E3\u4E86\u3002</p><h2 id="\u7B2C\u4E09\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u516C\u94A5\uFF09\u{1F31F}" tabindex="-1">\u7B2C\u4E09\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u516C\u94A5\uFF09\u{1F31F} <a class="header-anchor" href="#\u7B2C\u4E09\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u516C\u94A5\uFF09\u{1F31F}" aria-hidden="true">#</a></h2><p>\u4ECE\u4E0A\u4E00\u5C42\u53EF\u4EE5\u77E5\u9053\uFF0C\u8981\u4FDD\u8BC1\u6570\u636E\u7684\u5B89\u5168\uFF0C\u5C31\u5FC5\u987B\u5F97\u4FDD\u8BC1\u670D\u52A1\u5668\u7ED9\u5BA2\u6237\u7AEF\u4E0B\u53D1\u7684\u516C\u94A5\u662F\u771F\u6B63\u7684\u516C\u94A5\uFF0C\u800C\u4E0D\u662F\u4E2D\u95F4\u4EBA\u4F2A\u9020\u7684\u516C\u94A5\u3002\u90A3\u600E\u4E48\u4FDD\u8BC1\u5462\uFF1F</p><p>\u90A3\u5C31\u5F97\u5F15\u5165\u6570\u5B57\u8BC1\u4E66\u4E86\uFF0C\u6570\u5B57\u8BC1\u4E66\u662F\u670D\u52A1\u5668\u4E3B\u52A8\u53BB\u6743\u5A01\u673A\u6784\u7533\u8BF7\u7684\uFF0C\u8BC1\u4E66\u4E2D\u5305\u542B\u4E86\u4E0A\u4E00\u4E2A\u56FE\u4E2D\u7684\u52A0\u5BC6\u8FC7\u7684 A \u516C\u94A5\u548C\u6743\u5A01\u673A\u6784\u7684\u4FE1\u606F\uFF0C\u6240\u4EE5\u670D\u52A1\u5668\u53EA\u9700\u8981\u7ED9\u5BA2\u6237\u7AEF\u4E0B\u53D1\u6570\u5B57\u8BC1\u4E66\u5373\u53EF\u3002\u73B0\u5728\u6D41\u7A0B\u56FE\u5982\u4E0B\uFF1A</p><p><img src="https://alvin-cdn.oss-cn-shenzhen.aliyuncs.com/images/https-certificate4.png" alt=""></p><p>\u90A3\u6570\u5B57\u8BC1\u4E66\u4E2D\u7684 A \u516C\u94A5\u662F\u5982\u4F55\u52A0\u5BC6\u7684\u5462\uFF1F</p><p>\u7B54\u6848\u662F\u975E\u5BF9\u79F0\u52A0\u5BC6\uFF0C\u53EA\u4E0D\u8FC7\u8FD9\u91CC\u662F\u4F7F\u7528\u53EA\u6709\u6743\u5A01\u673A\u6784\u81EA\u5DF1\u624D\u6709\u7684\u79C1\u94A5\u52A0\u5BC6\u3002</p><p>\u7B49\u4E00\u4E0B\uFF0C\u65E2\u7136 A \u516C\u94A5\u88AB\u6743\u5A01\u673A\u6784\u7684\u79C1\u94A5\u52A0\u5BC6\u4E86\uFF0C\u90A3\u5BA2\u6237\u7AEF\u6536\u5230\u8BC1\u4E66\u4E4B\u540E\u600E\u4E48\u89E3\u5BC6\u8BC1\u4E66\u4E2D\u7684 A \u516C\u94A5\u5462?\u9700\u8981\u6709\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\u624D\u80FD\u89E3\u5BC6\u554A\uFF01\u90A3\u8FD9\u4E2A\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\u53C8\u662F\u600E\u4E48\u5B89\u5168\u5730\u4F20\u8F93\u7ED9\u5BA2\u6237\u7AEF\u7684\u5462\uFF1F\u611F\u89C9\u8FDB\u5165\u4E86\u9E21\u751F\u86CB\uFF0C\u86CB\u751F\u9E21\u7684\u6096\u8BBA\u4E86~~</p><p>\u522B\u614C\uFF0C<strong>\u7B54\u6848\u662F\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\u4E0D\u9700\u8981\u4F20\u8F93\uFF0C\u56E0\u4E3A\u6743\u5A01\u673A\u6784\u4F1A\u548C\u4E3B\u6D41\u7684\u6D4F\u89C8\u5668\u6216\u64CD\u4F5C\u7CFB\u7EDF\u5408\u4F5C\uFF0C\u5C06\u4ED6\u4EEC\u7684\u516C\u94A5\u5185\u7F6E\u5728\u6D4F\u89C8\u5668\u6216\u64CD\u4F5C\u7CFB\u7EDF\u73AF\u5883\u4E2D</strong>\u3002\u5BA2\u6237\u7AEF\u6536\u5230\u8BC1\u4E66\u4E4B\u540E\uFF0C\u53EA\u9700\u8981\u4ECE\u8BC1\u4E66\u4E2D\u627E\u5230\u6743\u5A01\u673A\u6784\u7684\u4FE1\u606F\uFF0C\u5E76\u4ECE\u672C\u5730\u73AF\u5883\u4E2D\u627E\u5230\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\uFF0C\u5C31\u80FD\u6B63\u786E\u89E3\u5BC6 A \u516C\u94A5\u3002</p><p>\u8FD9\u6837\u5C31\u7EDD\u5BF9\u5B89\u5168\u4E86\u5417\uFF1F\u65E2\u7136\u6743\u5A01\u6280\u80FD\u80FD\u7ED9\u670D\u52A1\u5668\u7B7E\u53D1\u6570\u5B57\u8BC1\u4E66\uFF0C\u90A3\u4E3A\u4EC0\u4E48\u5C31\u4E0D\u53EF\u80FD\u7ED9\u4E2D\u95F4\u4EBA\u7B7E\u53D1\u6570\u5B57\u8BC1\u4E66\u5462?\u6BD5\u7ADF\u8D5A\u94B1\u7684\u751F\u610F\u6743\u5A01\u673A\u6784\u4E5F\u4E0D\u4F1A\u62D2\u7EDD\u7684\u5440\u3002</p><p>\u8BD5\u60F3\u4E00\u4E0B\uFF1A</p><p>\u670D\u52A1\u5668\u7ED9\u5BA2\u6237\u7AEF\u4E0B\u53D1\u6570\u5B57\u8BC1\u4E66\u65F6\u8BC1\u4E66\u88AB\u4E2D\u95F4\u4EBA\u52AB\u6301\u4E86\uFF0C\u4E2D\u95F4\u4EBA\u5C06\u670D\u52A1\u5668\u7684\u8BC1\u4E66\u66FF\u6362\u6210\u81EA\u5DF1\u7684\u8BC1\u4E66\u4E0B\u53D1\u7ED9\u5BA2\u6237\u7AEF\uFF0C\u5BA2\u6237\u7AEF\u6536\u5230\u4E4B\u540E\u80FD\u591F\u901A\u8FC7\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\u89E3\u5BC6\u8BC1\u4E66\u5185\u5BB9\uFF08\u56E0\u4E3A\u4E2D\u95F4\u4EBA\u7684\u8BC1\u4E66\u4E5F\u662F\u6743\u5A01\u673A\u6784\u79C1\u94A5\u52A0\u5BC6\u7684\uFF09\uFF0C\u4ECE\u800C\u83B7\u53D6\u516C\u94A5\uFF0C\u4F46\u662F\uFF0C\u8FD9\u91CC\u7684\u516C\u94A5\u5E76\u4E0D\u662F\u670D\u52A1\u5668\u539F\u672C\u7684 A \u516C\u94A5\uFF0C\u800C\u662F\u4E2D\u95F4\u4EBA\u81EA\u5DF1\u8BC1\u4E66\u4E2D\u7684 B \u516C\u94A5\u3002\u4ECE\u7B2C\u4E8C\u5C42\u53EF\u77E5\uFF0C\u5982\u679C\u4E0D\u80FD\u4FDD\u8BC1\u5BA2\u6237\u7AEF\u6536\u5230\u7684\u516C\u94A5\u662F\u670D\u52A1\u5668\u4E0B\u53D1\u7684\uFF0C\u90A3\u6574\u4E2A\u901A\u4FE1\u6570\u636E\u7684\u5B89\u5168\u5C31\u6CA1\u6CD5\u4FDD\u8BC1\u3002\u7B80\u5355\u603B\u7ED3\u5C31\u662F\u8BC1\u4E66\u88AB\u8C03\u5305~</p><p>\u6240\u4EE5\uFF0C\u8FD8\u5F97\u4FDD\u8BC1\u5BA2\u6237\u7AEF\u6536\u5230\u7684\u8BC1\u4E66\u5C31\u662F\u670D\u52A1\u5668\u4E0B\u53D1\u7684\u8BC1\u4E66\uFF0C\u6CA1\u6709\u88AB\u4E2D\u95F4\u4EBA\u7BE1\u6539\u8FC7\u3002</p><h2 id="\u7B2C\u56DB\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u8BC1\u4E66\uFF09\u{1F31F}" tabindex="-1">\u7B2C\u56DB\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u8BC1\u4E66\uFF09\u{1F31F} <a class="header-anchor" href="#\u7B2C\u56DB\u5C42\uFF08\u5B89\u5168\u4F20\u8F93\u8BC1\u4E66\uFF09\u{1F31F}" aria-hidden="true">#</a></h2><p>\u8FD9\u4E00\u5C42\uFF0C\u6211\u4EEC\u7684\u4EFB\u52A1\u662F\uFF1A<strong>\u4FDD\u8BC1\u5BA2\u6237\u7AEF\u6536\u5230\u7684\u8BC1\u4E66\u662F\u670D\u52A1\u5668\u4E0B\u53D1\u7684\u8BC1\u4E66\uFF0C\u6CA1\u6709\u88AB\u4E2D\u95F4\u4EBA\u7BE1\u6539\u8FC7</strong>\u3002</p><p>\u6240\u4EE5\uFF0C\u8FD9\u91CC\u5C31\u6709\u4E24\u4E2A\u9700\u6C42:</p><ul><li>\u8BC1\u660E\u8BC1\u4E66\u5185\u5BB9\u6CA1\u6709\u88AB\u7B2C\u4E09\u65B9\u7BE1\u6539\u8FC7\uFF1B</li><li>\u8BC1\u660E\u8BC1\u4E66\u662F\u670D\u52A1\u5668\u4E0B\u53D1\u7684</li></ul><p>\u5176\u5B9E\u8FD9\u4E9B\u95EE\u9898\uFF0C\u6570\u5B57\u8BC1\u4E66\u672C\u8EAB\u5DF2\u7ECF\u63D0\u4F9B\u65B9\u6848\u4E86\uFF0C\u6570\u5B57\u8BC1\u4E66\u4E2D\u9664\u4E86\u5305\u542B\u52A0\u5BC6\u4E4B\u540E\u7684\u670D\u52A1\u5668\u516C\u94A5\uFF0C\u6743\u5A01\u673A\u6784\u7684\u4FE1\u606F\u4E4B\u5916\uFF0C\u8FD8\u5305\u542B\u4E86\u8BC1\u4E66\u5185\u5BB9\u7684\u7B7E\u540D</p><p>\u5148\u901A\u8FC7 Hash \u51FD\u6570\u8BA1\u7B97\u5F97\u5230\u8BC1\u4E66\u6570\u5B57\u6458\u8981\uFF0C\u7136\u540E\u7528\u6743\u5A01\u673A\u6784\u79C1\u94A5\u52A0\u5BC6\u6570\u5B57\u6458\u8981\u5F97\u5230\u6570\u5B57\u7B7E\u540D\uFF0C\u7B7E\u540D\u8BA1\u7B97\u65B9\u6CD5\u4EE5\u53CA\u8BC1\u4E66\u5BF9\u5E94\u7684\u57DF\u540D\u3002\u8FD9\u6837\u4E00\u6765\uFF0C\u5BA2\u6237\u7AEF\u6536\u5230\u8BC1\u4E66\u4E4B\u540E\uFF1A</p><ul><li>\u4F7F\u7528\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\u89E3\u5BC6\u6570\u5B57\u8BC1\u4E66\uFF0C\u5F97\u5230\u8BC1\u4E66\u5185\u5BB9\uFF08\u670D\u52A1\u5668\u7684\u516C\u94A5\uFF09\u4EE5\u53CA\u8BC1\u4E66\u7684\u6570\u5B57\u7B7E\u540D\uFF0C\u7136\u540E\u6839\u636E\u8BC1\u4E66\u4E0A\u63CF\u8FF0\u7684\u8BA1\u7B97\u8BC1\u4E66\u7B7E\u540D\u7684\u65B9\u6CD5\u8BA1\u7B97\u4E00\u4E0B\u5F53\u524D\u8BC1\u4E66\u7684\u7B7E\u540D\uFF0C\u4E0E\u6536\u5230\u7684\u7B7E\u540D\u4F5C\u5BF9\u6BD4\uFF0C\u5982\u679C\u4E00\u6837\uFF0C\u8868\u793A\u8BC1\u4E66\u4E00\u5B9A\u662F\u670D\u52A1\u5668\u4E0B\u53D1\u7684\uFF0C\u6CA1\u6709\u88AB\u4E2D\u95F4\u4EBA\u7BE1\u6539\u8FC7\u3002 <ul><li>\u56E0\u4E3A\u4E2D\u95F4\u4EBA\u867D\u7136\u6709\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\uFF0C\u80FD\u591F\u89E3\u6790\u8BC1\u4E66\u5185\u5BB9\u5E76\u7BE1\u6539\uFF0C\u4F46\u662F\u7BE1\u6539\u5B8C\u6210\u4E4B\u540E\u4E2D\u95F4\u4EBA\u9700\u8981\u5C06\u8BC1\u4E66\u91CD\u65B0\u52A0\u5BC6\uFF0C\u4F46\u662F\u4E2D\u95F4\u4EBA\u6CA1\u6709\u6743\u5A01\u673A\u6784\u7684\u79C1\u94A5\uFF0C\u65E0\u6CD5\u52A0\u5BC6\uFF0C\u5F3A\u884C\u52A0\u5BC6\u53EA\u4F1A\u5BFC\u81F4\u5BA2\u6237\u7AEF\u65E0\u6CD5\u89E3\u5BC6\uFF0C\u5982\u679C\u4E2D\u95F4\u4EBA\u5F3A\u884C\u4E71\u4FEE\u6539\u8BC1\u4E66\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u8BC1\u4E66\u5185\u5BB9\u548C\u8BC1\u4E66\u7B7E\u540D\u4E0D\u5339\u914D\u3002<strong>\u6240\u4EE5\u8BC1\u4E66\u7B7E\u540D\u5C31\u80FD\u5224\u65AD\u8BC1\u4E66\u662F\u5426\u88AB\u7BE1\u6539</strong></li><li>\u9664\u975E\u9ED1\u5BA2\u8DB3\u591F\u725B\u903C\uFF0C\u80FD\u7533\u8BF7\u4E00\u4EFD\u4E00\u6A21\u4E00\u6837\u7684\u8BC1\u4E66\uFF0C\u5E76\u4E14\u88AB\u52A0\u5230\u6D4F\u89C8\u5668\u7684\u4FE1\u4EFB\u8BC1\u4E66\u5217\u8868\u91CC\uFF0C\u8FD8\u8981\u901A\u8FC7\u8BC1\u4E66\u900F\u660E\u5EA6\u670D\u52A1\u7684\u76D1\u7763</li></ul></li><li>\u518D\u8003\u8651\u8BC1\u4E66\u88AB\u6389\u5305\u7684\u60C5\u51B5\uFF1A\u4E2D\u95F4\u4EBA\u540C\u6837\u53EF\u4EE5\u5411\u6743\u5A01\u673A\u6784\u7533\u8BF7\u4E00\u4EFD\u8BC1\u4E66\uFF0C\u7136\u540E\u5728\u670D\u52A1\u5668\u7ED9\u5BA2\u6237\u7AEF\u4E0B\u53D1\u8BC1\u4E66\u7684\u65F6\u5019\u52AB\u6301\u539F\u8BC1\u4E66\uFF0C\u5C06\u81EA\u5DF1\u7684\u5047\u8BC1\u4E66\u4E0B\u53D1\u7ED9\u5BA2\u6237\u7AEF\uFF0C\u5BA2\u6237\u7AEF\u6536\u5230\u4E4B\u540E\u4F9D\u7136\u80FD\u591F\u4F7F\u7528\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\u89E3\u5BC6\u8BC1\u4E66\uFF0C\u5E76\u4E14\u8BC1\u4E66\u7B7E\u540D\u4E5F\u6CA1\u95EE\u9898\u3002\u4F46\u662F\u8FD9\u4E2A\u65F6\u5019\u5BA2\u6237\u7AEF\u8FD8\u9700\u8981\u68C0\u67E5\u8BC1\u4E66\u4E2D\u7684\u57DF\u540D\u548C\u5F53\u524D\u8BBF\u95EE\u7684\u57DF\u540D\u662F\u5426\u4E00\u81F4\u3002\u5982\u679C\u4E0D\u4E00\u81F4\uFF0C\u4F1A\u53D1\u51FA\u8B66\u544A\uFF01</li></ul><p>\u4ECE\u4E0A\u9762\u7684\u5206\u6790\u53EF\u4EE5\u770B\u5230\uFF0C\u6570\u5B57\u8BC1\u4E66\u4E2D\u7684\u4FE1\u606F\u786E\u5B9E\u80FD\u8BA9\u5BA2\u6237\u7AEF\u8FA8\u522B\u8BC1\u4E66\u7684\u771F\u4F2A\u3002</p><hr><p>\u6574\u4E2A\u8FC7\u7A0B\u90FD\u662F\u5BA2\u6237\u7AEF\u600E\u4E48\u6838\u5B9E\u8BC1\u4E66\u662F\u5426\u6709\u6548\uFF0C\u6709\u6548\u5219\u8BC1\u660E\u516C\u94A5\u662F\u6B63\u786E\u7684\u3002\u8BC1\u4E66\u5230\u624B\uFF0C\u6743\u5A01\u673A\u6784\u7684\u516C\u94A5\u4E0D\u9700\u8981\u4F20\u8F93\uFF0C\u56E0\u4E3A\u6743\u5A01\u673A\u6784\u4F1A\u548C\u4E3B\u6D41\u7684\u6D4F\u89C8\u5668\u6216\u64CD\u4F5C\u7CFB\u7EDF\u5408\u4F5C\uFF0C\u5C06\u4ED6\u4EEC\u7684\u516C\u94A5\u5185\u7F6E\u5728\u6D4F\u89C8\u5668\u6216\u64CD\u4F5C\u7CFB\u7EDF\u73AF\u5883\u4E2D\u3002\u6240\u4EE5\u6D4F\u89C8\u5668\u5185\u7F6E\u516C\u94A5\uFF0C\u53EF\u4EE5\u89E3\u5BC6\u3002</p><p>\u9ED1\u5BA2\u4E5F\u60F3\u62FF\u5230\u516C\u94A5\uFF0C\u89C1\u4E0A\u3002CA \u8BC1\u4E66\u4E0D\u4F1A\u4E71\u9881\u53D1\uFF0C\u6240\u4EE5\u9ED1\u5BA2\u62FF\u5230\u8BC1\u4E66\u4E5F\u65E0\u53EF\u5948\u4F55\u3002</p><p>\u81F3\u4E8E\u670D\u52A1\u7AEF\u600E\u4E48\u786E\u8BA4\u5BA2\u6237\u7AEF\u8EAB\u4EFD\u3001token \u57DF\u540D\u90A3\u4E9B\u5C31\u53EF\u4EE5\u786E\u8BA4\u4E86\u3002</p>',45),s=[a];function n(r,o,c,h,d,_){return p(),e("div",null,s)}const m=t(l,[["render",n]]);export{g as __pageData,m as default};
